package com.szy;import com.alibaba.fastjson.serializer.SerializerFeature;import com.alibaba.fastjson.support.config.FastJsonConfig;import com.szy.util.DBUtil;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.EnableAutoConfiguration;import org.springframework.boot.autoconfigure.SpringBootApplication;import org.springframework.boot.autoconfigure.web.HttpMessageConverters;import org.springframework.boot.web.servlet.ServletComponentScan;import org.springframework.context.ApplicationContext;import org.springframework.context.annotation.Bean;import org.springframework.http.converter.HttpMessageConverter;@SpringBootApplication@EnableAutoConfiguration@ServletComponentScanpublic class ApplicationStart {	private static final Logger logger = LoggerFactory.getLogger(ApplicationStart.class);	private static ApplicationContext context;	public static <T> T getBean(Class<T> clazz){		return context.getBean(clazz);	}	@Bean	public HttpMessageConverters fastJsonHttpMessageConverters() {		DivisionHttpMessageConverter httpConverter = new DivisionHttpMessageConverter();		FastJsonConfig fastJsonConfig = new FastJsonConfig();		fastJsonConfig.setSerializerFeatures(SerializerFeature.PrettyFormat);		httpConverter.setFastJsonConfig(fastJsonConfig);		HttpMessageConverter<?> converter = httpConverter;		return new HttpMessageConverters(converter);	}	public static void main(String[] args) {		context = SpringApplication.run(ApplicationStart.class, args);		DBUtil.setContext(context);		logger.info("程序启动成功！");	}}